#!/usr/bin/env python3


def maxSubArray(A):
    largestSum = -999999999999999999
    for i in range(0, len(A)):
        for j in range(i+1, len(A)+1):
            if (sum(A[i:j]) > largestSum):
                largestSum = sum(A[i:j])
                #print("sum(A[{},{}])=={}".format(i, j, sum(largestArray)))
    return(largestSum)


